// Utility functions for placeholder images during development

// CSS-based placeholder images using data URLs
export function getPlaceholderImage(width: number, height: number, text?: string): string {
  const bgColor = 'f3f4f6'; // Light gray
  const textColor = '6b7280'; // Gray
  const encodedText = text ? encodeURIComponent(text) : '';
  
  // Create a simple SVG placeholder
  const svg = `
    <svg width="${width}" height="${height}" xmlns="http://www.w3.org/2000/svg">
      <rect width="100%" height="100%" fill="#${bgColor}"/>
      <text x="50%" y="50%" font-family="Arial, sans-serif" font-size="16" fill="#${textColor}" text-anchor="middle" dy=".3em">${encodedText}</text>
    </svg>
  `;
  
  return `data:image/svg+xml;base64,${btoa(svg)}`;
}

export function getProductImage(productName: string, index: number = 1): string {
  return getPlaceholderImage(400, 400, `${productName} ${index}`);
}

export function getCategoryImage(categoryName: string): string {
  return getPlaceholderImage(600, 400, categoryName);
}

export function getHeroImage(): string {
  return getPlaceholderImage(1200, 600, 'California Coastal Landscape');
}

export function getAboutImage(): string {
  return getPlaceholderImage(800, 600, 'About Abbot Kinney');
}

// Simple color-based placeholders for better performance
export const PLACEHOLDER_IMAGES = {
  product: 'data:image/svg+xml;base64,',
  category: 'data:image/svg+xml;base64,',
  hero: 'data:image/svg+xml;base64,',
  about: 'data:image/svg+xml;base64,'
}; 